import React,{Component} from 'react';
import './App.css';
import Person from './person/Person';

class App extends Component() {
    state = {
        person: [
            {id: '1', name: 'john', age: 40},
            {id: '2', name: 'joe', age: 50},
            {id: '3', name: 'doe', age: 60}
        ],
        showPerson: false
    };

    toggleHandler = (event) => {
        this.state.showPerson= true;
    };

    render() {
        let persons = {};

        if(this.state.showPerson) {
            persons = (
                <div>
                    this.state.persons.map
                    <Person name='john' age='40'/>
                    <Person name='joe' age='50'/>
                    <Person name='doe' age='60'/>
                </div>
            )
        }

        return (
            <div className="App">
                <h1>hey welcome</h1>
                <button onClick={this.toggleHandler}></button>
            </div>
        );
    }
}

export default App;
